import React, { useCallback, useState } from "react";
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  addEdge,
} from "reactflow";
import "reactflow/dist/style.css";
import CustomNode from "./CustomNode";
import './App.css';

const initialNodes = [];
const initialEdges = [];

const nodeTypes = {
  customNode: CustomNode,
};

function FlowComponent() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [nextNodeId, setNextNodeId] = useState(3);
  const [nodeType, setNodeType] = useState("");
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [popupData, setPopupData] = useState({
    dropdownValue: "",
    numberValue: "",
    stringValue: ""
  });

  const onConnect = useCallback(
    (params) => setEdges((els) => addEdge(params, els)),
    [setEdges]
  );

  const addNode = useCallback(() => {
    if (!nodeType) return;
    setIsPopupOpen(true);
  }, [nodeType]);

  const handlePopupChange = (field, value) => {
    setPopupData((prevData) => ({
      ...prevData,
      [field]: value
    }));
  };

  const handlePopupSubmit = () => {
    const newNode = {
      id: String(nextNodeId),
      type: "customNode",
      data: {
        label: `${nodeType} Node ${nextNodeId}`,
        ...popupData,
        onDropdownChange: (value) => {
          setNodes((nds) =>
            nds.map((node) =>
              node.id === String(nextNodeId)
                ? { ...node, data: { ...node.data, dropdownValue: value } }
                : node
            )
          );
        },
        onNumberChange: (value) => {
          setNodes((nds) =>
            nds.map((node) =>
              node.id === String(nextNodeId)
                ? { ...node, data: { ...node.data, numberValue: value } }
                : node
            )
          );
        },
        onStringChange: (value) => {
          setNodes((nds) =>
            nds.map((node) =>
              node.id === String(nextNodeId)
                ? { ...node, data: { ...node.data, stringValue: value } }
                : node
            )
          );
        },
      },
      position: {
        x: (Math.random() * window.innerWidth) / 2,
        y: (Math.random() * window.innerHeight) / 2,
      },
    };
    setNodes((nds) => nds.concat(newNode));
    setNextNodeId(nextNodeId + 1);
    setNodeType("");
    setIsPopupOpen(false);
  };

  return (
    <div style={{ height: "100vh", display: "flex", flexDirection: "column" }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        nodeTypes={nodeTypes}
        fitView
        style={{ flexGrow: 1 }}
      >
        <MiniMap />
        <Controls />
        <Background color="#aaa" gap={16} />
      </ReactFlow>
      <div
        style={{
          position: "absolute",
          right: "10px",
          top: "10px",
          zIndex: 1000,
        }}
      >
        <select value={nodeType} onChange={(e) => setNodeType(e.target.value)}>
          <option value="">Select Node Type...</option>
          <option value="Select Dataset">Select Dataset</option>
          <option value="Pre-processing">Pre-processing</option>
          <option value="Train">Train</option>
          <option value="Test">Test</option>
          <option value="Deploy">Deploy</option>
        </select>
        <button onClick={addNode}>Add Node</button>
      </div>
      {isPopupOpen && (
        <div className="popup">
          <label>Dropdown:</label>
          <select
            value={popupData.dropdownValue}
            onChange={(e) => handlePopupChange("dropdownValue", e.target.value)}
          >
            <option value="">Select...</option>
            <option value="Option 1">Option 1</option>
            <option value="Option 2">Option 2</option>
            <option value="Option 3">Option 3</option>
          </select>
          <label>Number:</label>
          <input
            type="number"
            value={popupData.numberValue}
            onChange={(e) => handlePopupChange("numberValue", e.target.value)}
          />
          <label>String:</label>
          <input
            type="text"
            value={popupData.stringValue}
            onChange={(e) => handlePopupChange("stringValue", e.target.value)}
          />
          <button onClick={handlePopupSubmit}>Submit</button>
        </div>
      )}
    </div>
  );
}

export default FlowComponent;
