// App.js
import React, { useCallback, useState } from "react";
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  addEdge,
} from "reactflow";
import "reactflow/dist/style.css";
import CustomNode from "./CustomNode";

const initialNodes = [];
const initialEdges = [];

const nodeTypes = {
  customNode: CustomNode,
};

function FlowComponent() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [nextNodeId, setNextNodeId] = useState(3);
  const [nodeType, setNodeType] = useState("");
  const [selectedNode, setSelectedNode] = useState(null);
  const [submittedData, setSubmittedData] = useState(null);
  const [number1, setNumber1] = useState(null);
  const [number2, setNumber2] = useState(null);
  const [result, setResult] = useState(null);

  const onConnect = useCallback(
    (params) => setEdges((els) => addEdge(params, els)),
    [setEdges]
  );

  const onNodeClick = useCallback((event, node) => {
    setSelectedNode(node);
  }, []);

  const executeAction = useCallback(() => {
    if (number1 !== null && number2 !== null) {
      const sum = parseFloat(number1) + parseFloat(number2);
      setResult(sum);
    }
  }, [number1, number2]);

  const addNode = useCallback(() => {
    if (!nodeType) return;
    const newNode = {
      id: String(nextNodeId),
      type: "customNode",
      data: {
        label: `${nodeType} Node ${nextNodeId}`,
        dropdownValue: "",
        numberValue: "",
        stringValue: "",
        onDropdownChange: (value) => {
          setNodes((nds) =>
            nds.map((node) =>
              node.id === String(nextNodeId)
                ? { ...node, data: { ...node.data, dropdownValue: value } }
                : node
            )
          );
        },
        onNumberChange: (value) => {
          setNodes((nds) =>
            nds.map((node) =>
              node.id === String(nextNodeId)
                ? { ...node, data: { ...node.data, numberValue: value } }
                : node
            )
          );
        },
        onStringChange: (value) => {
          setNodes((nds) =>
            nds.map((node) =>
              node.id === String(nextNodeId)
                ? { ...node, data: { ...node.data, stringValue: value } }
                : node
            )
          );
        },
      },
      position: {
        x: (Math.random() * window.innerWidth) / 2,
        y: (Math.random() * window.innerHeight) / 2,
      },
    };
    setNodes((nds) => nds.concat(newNode));
    setNextNodeId(nextNodeId + 1);
    setNodeType("");
  }, [nodeType, nextNodeId, setNodes]);

  const updateNodeData = useCallback(
    (e) => {
      const newUrl = e.target.value;
      setSelectedNode((prevNode) => ({
        ...prevNode,
        data: { ...prevNode.data, url: newUrl, label: newUrl },
      }));
      if (selectedNode.type === "Select Dataset") {
        if (!number1) {
          setNumber1(newUrl);
        } else {
          setNumber2(newUrl);
        }
      }
    },
    [selectedNode, number1]
  );

  const handleSubmit = useCallback(() => {
    if (selectedNode) {
      setNodes((currentNodes) =>
        currentNodes.map((node) =>
          node.id === selectedNode.id
            ? {
                ...node,
                data: {
                  ...node.data,
                  url: selectedNode.data.url,
                  label: selectedNode.data.url,
                },
              }
            : node
        )
      );
      setSubmittedData(selectedNode.data);
      setSelectedNode(null); // Optional: Clear selection after submit
    }
  }, [selectedNode, setNodes]);

  return (
    <div style={{ height: "100vh", display: "flex", flexDirection: "column" }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        onNodeClick={onNodeClick}
        nodeTypes={nodeTypes}
        fitView
        style={{ flexGrow: 1 }}
      >
        <MiniMap />
        <Controls />
        <Background color="#aaa" gap={16} />
      </ReactFlow>
      <div
        style={{
          position: "absolute",
          right: "10px",
          top: "10px",
          zIndex: 1000,
        }}
      >
        <select value={nodeType} onChange={(e) => setNodeType(e.target.value)}>
          <option value="">Select Node Type...</option>
          <option value="Select Dataset">Select Dataset</option>
          <option value="Pre-processing">Pre-processing</option>
          <option value="Train">Train</option>
          <option value="Test">Test</option>
          <option value="Deploy">Deploy</option>
        </select>
        <button onClick={addNode}>Add Node</button>
        <button onClick={executeAction}>Execute Action</button>
      </div>
      {submittedData && (
        <div
          style={{
            position: "absolute",
            right: "10px",
            top: "160px",
            zIndex: 1000,
            background: "white",
            padding: "10px",
            border: "1px solid #ddd",
          }}
        >
          <div>
            <strong>Submitted URL:</strong> {submittedData.url}
          </div>
          <div>
            <strong>Result:</strong> {result}
          </div>
        </div>
      )}
    </div>
  );
}

export default FlowComponent;
